import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { API_URL } from '../config.json';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function JoinMeeting(){
    var [count, setCount] = useState(0);
    var [ jwt, setJwt ] = useState("");

    const { joinToken, mobileAppJwt } = useParams();

    useEffect(() => {
        if(localStorage.getItem("logined") == false){
          //window.location="/login";
          setJwt(mobileAppJwt);
          alert("welcome to web site in app")

        }else if(localStorage.getItem("logined") == true){
            setJwt(localStorage.getItem("jwt"));

        }else if(localStorage.getItem("logined") == "false") {
          //window.location="/login";
          setJwt(mobileAppJwt);
          alert("welcome to web site in app")

        }else {
            setJwt(localStorage.getItem("jwt"));
            alert("bilinmeyen değer. value: " + localStorage.getItem("logined"));
        }
    }, []) //Logined push

    useEffect(() => {
        setCount(count + 1);
        alert("jwt", jwt);

        if(jwt == null){

        }else{
        axios.post(`${API_URL}/join-invite`, {
            token: joinToken
        }, {
            headers: {
                "Authorization": "Bearer " + jwt
            }
        })
        .then((res) => {
            console.log(res.data);

            toast.dark(res.data.message, {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,                
            });

            setTimeout(function(){
                if(res.data.auth == false){
                    //window.location="/login";    
                }                    

            }, 2000)



        })
    }

    }, [joinToken])

    return(
        <div className="JoinMeeting">
            <h4>mobile app jwt: {mobileAppJwt}</h4>
            <h4>jwt: {jwt}</h4>
            <ToastContainer
                position="top-right"
                autoClose={5000}
                hideProgressBar={false}
                newestOnTop={false}
                closeOnClick
                rtl={false}
                pauseOnFocusLoss
                draggable
                pauseOnHover
            />



            <h6>Lütfen bekleyin...</h6>

        </div>
    )
}

export default JoinMeeting;
