import React, { useEffect, useState } from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useHistory
} from "react-router-dom";
import logo from './logo.svg';
import './App.css';
import { API_URL } from './config'
import {toast, ToastContainer} from 'react-toastify';

//Pages
import HomePage from './components/Home';
import LoginPage from './components/Login';
import RegisterPage from './components/Register';
import LogoutPage from './components/Logout';
import CreateInvitePage from './components/CreateMeeting';
import MeetingPage from './components/Meeting';
import JoinMeeting from './components/JoinMeeting';
import Discover from './components/Discover';
import Profile from './components/Profile';


//Menu
import Navbar from './components/menu/Navbar';

export default function App() {
  var [logined, setLogined] = useState(false)
  var [index, setIndex] = useState(0);
  let history = useHistory();

  useEffect(() => {

	if(window.innerWidth < 629){
		alert("Telefonunuzun boyutu gereğinden küçük. Bazı tasarım hataları ile karşılaşmamak için Big Meeting'i lütfen daha büyük bir telefondan kullanınız")
	}

    setLogined(localStorage.getItem("logined"));

	if(localStorage.getItem("logined" == "true" || localStorage.getItem("logined") == true)){
		fetch(`${API_URL}/meeting/60be37babade743a0cef515`, {

			method: "GET",

			headers: {

			"Authorization":"Bearer " + localStorage.getItem("jwt")
			}

		})
		.then((res) => {
			console.log(res);
		})
		.catch((err) => {
			console.log("request error: ", err);

		})
	}

    fetch(`${API_URL}/protected`, {
			headers: {
				"Authorization": "Bearer " + localStorage.getItem("jwt")
			}
		}).then((res) => res.json())
		.then((data) => {
			console.log("TOKEN Verify", data);

			if(data.error == null && data.auth == true){
				//alert("auth is true broo yeaah");
				localStorage.setItem("logined", true);
				setLogined(localStorage.getItem("logined"))

				history.push("/");
				window.location="/";

			}else if(data.error && data.auth == false){
				console.log("auth is false bro bruhh xD");

				localStorage.setItem("jwt", null);
				localStorage.setItem("user", null);
				localStorage.setItem("userId", null);
				localStorage.setItem("logined", false);

				history.push("/login");
				window.location="/login";

				console.log(data.message)
				toast.dark(data.message, {
					position: "top-right",
					autoClose: 5000,
					hideProgressBar: false,
					closeOnClick: true,
					pauseOnHover: true,
					draggable: true,
					progress: undefined, 					
				});					


			}else if(data.auth == "false"){
				console.log("false bro bruh .d");
				window.location="/login";
				history.push("/login");

			}else{
				console.log("auth is false bro bruhh xD");

				localStorage.setItem("jwt", null);
				localStorage.setItem("user", null);
				localStorage.setItem("userId", null);
				localStorage.setItem("logined", false);

				history.push("/login");
				window.location="/login";

				console.log(data.message)
				toast.dark(data.message, {
					position: "top-right",
					autoClose: 5000,
					hideProgressBar: false,
					closeOnClick: true,
					pauseOnHover: true,
					draggable: true,
					progress: undefined, 
				}); 


			}
		})
		.catch((err) => {
			////console.log("VERIFY TOKEN ERROR!!" + err)
		})


  } )

  return (
    <Router>
      <div>
		<ToastContainer
			position="top-right"
			autoClose={5000}
			hideProgressBar={false}
			newestOnTop={false}
			closeOnClick
			rtl={false}
			pauseOnFocusLoss
			draggable
			pauseOnHover
		/>

	  	<Navbar className="navbar-menu" style={{width: "100%", position: "fixed"}} />

        <br/>

        <Switch>
		  <Route path="/profile">
			  <Profile />
		  </Route>
          <Route path="/discover">
			<Discover />
		  </Route>
		  <Route path="/join-meeting/:joinToken">
			<JoinMeeting />
		  </Route>
		  <Route path="/meeting/:meetingId">
			<MeetingPage />
		  </Route>
          <Route path="/login">
            <LoginPage />
          </Route>
          <Route path="/logout">
            <LogoutPage />
          </Route>
          <Route path="/register">
            <RegisterPage />
          </Route>
          <Route path="/create-meeting">
            <CreateInvitePage />
          </Route>
          <Route path="/">
            <HomePage />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}
