import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { API_URL } from '../config.json';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function JoinMeeting(){
    var [count, setCount] = useState(0);

    const { joinToken } = useParams();

    useEffect(() => {
        if(localStorage.getItem("logined") === false){
          window.location="/login";
        }else if(localStorage.getItem("logined") === true){

        }else if(localStorage.getItem("logined") == "false") {
          //alert("string false :D");
          window.location="/login";

        }else {
          //alert("bilinmeyen değer. value: " + typeof localStorage.getItem("logined"));
        }
        }, []) //Logined push

    useEffect(() => {
        setCount(count + 1);

        axios.post(`${API_URL}/join-invite`, {
            token: joinToken
        }, {
            headers: {
                "Authorization": "Bearer " + localStorage.getItem("jwt")
            }
        })
        .then((res) => {
            console.log(res.data)

            toast.dark(res.data.message, {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,                
            });

            setTimeout(function(){
                if(res.data.auth == false){
                    window.location="/login";    
                }                    

            }, 2000)



        })

    }, [joinToken])

    return(
        <div className="JoinMeeting">
            <ToastContainer
                position="top-right"
                autoClose={5000}
                hideProgressBar={false}
                newestOnTop={false}
                closeOnClick
                rtl={false}
                pauseOnFocusLoss
                draggable
                pauseOnHover
            />



            <h6>Lütfen bekleyiniz...</h6>

        </div>
    )
}

export default JoinMeeting;
